mapper.select:
select id,lineId,station
from t_line_station
where isShow = 'show' and lineId = #{lineId}

select *
from t_plan
where isShow = 'show' and name = #{name}

select id,name,lineId,lineNo,stationId,stationName
from t_plan
where isShow = 'show'

	and lineId = #{lineId}


	and stationId = #{stationId}


	and name = #{name}

order by id desc

mapper.update:
update t_planattach
set isShow = 'notShow'
where id ${str}

update t_plan
set name = #{name},lineId = #{lineId},lineNo = #{lineNo},stationId = #{stationId},stationName = #{stationName}
where id = #{id}

update t_plan
set isShow = 'notShow'
where id = #{id}
