mapper.select:
select 
id,occurDate,userName,operation
from
t_log
where isShow = 'show'

	and occurDate >= #{startDate} and occurDate <= #{endDate}


	and userName = #{userName}


	operation = #{content}

order by occurDate desc

mapper.update:
update t_log
set isShow = 'notShow'
where id ${str}

mapper.select:
select *
from t_role
where isShow = 'show' and name = #{name}

select id, name,description from t_role where isShow = 'show'
and name = #{name}
order by createDate desc

mapper.update:
update t_role
set
name = #{name},description = #{description}
where id = #{id}

mapper.select:
select * 
from t_usergroup
where isShow = 'show' and name = #{name}

select id,name,description from t_usergroup where isShow = 'show'
and name = #{name}
order by createDate desc

mapper.update:
update t_usergroup
set name = #{name},description = #{description}
where id = #{id}

update t_usergroup
set isShow = 'notShow'
where id = #{id}
