mapper.select:
select 
id,occurDate,userName,operation
from
t_log
where isShow = 'show'

	and occurDate >= #{startDate} and occurDate <= #{endDate}


	and userName = #{userName}


	and operation = #{content}

order by occurDate desc

mapper.update:
update t_log
set isShow = 'notShow'
where id ${str}

mapper.select:
select *
from t_role
where isShow = 'show' and name = #{name}

select id, name,description from t_role where isShow = 'show'
and name = #{name}
order by id desc

mapper.update:
update t_role
set
name = #{name},description = #{description}
where id = #{id}

mapper.select:
select * 
from t_usergroup
where isShow = 'show' and name = #{name}

select id,name,description from t_usergroup where isShow = 'show'
and name = #{name}
order by id desc

mapper.update:
update t_usergroup
set name = #{name},description = #{description}
where id = #{id}

update t_usergroup
set isShow = 'notShow'
where id = #{id}

update t_userinfo
set userGroupId = null,userGroupName = ''
where userGroupId = #{groupId}

mapper.select:
select
id,userName,roleName,userGroupName
from t_userinfo
where isShow = 'show'

	and userName = #{userName}


	and roleId = #{roleId}


	and userGroupId = #{userGroupId}

mapper.update:
update t_userinfo
set
userName = #{userName},roleId = #{roleId},roleName = #{roleName},userGroupId = #{userGroupId},userGroupName = #{userGroupName}
where id = #{userId}

update t_user_function
set isShow = 'notShow'
where userId = #{userId}

update t_userinfo
set isShow = 'notShow'
where id = #{userId}
